  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all test_models as @test_models" do
      test_model = TestModel.create! valid_attributes
      get :index
      assigns(:test_models).should eq([test_model])
    end
  end

  describe "GET show" do
    it "assigns the requested test_model as @test_model" do
      test_model = TestModel.create! valid_attributes
      get :show, :id => test_model.id.to_s
      assigns(:test_model).should eq(test_model)
    end
  end

  describe "GET new" do
    it "assigns a new test_model as @test_model" do
      get :new
      assigns(:test_model).should be_a_new(TestModel)
    end
  end

  describe "GET edit" do
    it "assigns the requested test_model as @test_model" do
      test_model = TestModel.create! valid_attributes
      get :edit, :id => test_model.id.to_s
      assigns(:test_model).should eq(test_model)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new TestModel" do
        expect {
          post :create, :test_model => valid_attributes
        }.to change(TestModel, :count).by(1)
      end

      it "assigns a newly created test_model as @test_model" do
        post :create, :test_model => valid_attributes
        assigns(:test_model).should be_a(TestModel)
        assigns(:test_model).should be_persisted
