      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        TestModel.any_instance.stub(:save).and_return(false)
        post :create, :test_model => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested test_model" do
        test_model = TestModel.create! valid_attributes
        # Assuming there are no other test_models in the database, this
